<?php/** * marketplace Admin Settings Class. * * @author 		webkul	 * @category 	Admin * @package 	webkul/Admin * @version     2.1.0 */if ( ! defined( 'ABSPATH' ) ) 	exit; // Exit if accessed directlyif ( ! class_exists( 'MP_Admin_Settings' ) ) :/** * MP_Admin_Settings */ class WC_Admin_Settings { private static $settings = array();	private static $errors   = array();	private static $messages = array();		public static function get_settings_pages() {		if ( empty( self::$settings ) ) {			$settings = array();			require_once( 'settings/class-mp-settings-page.php' );			$settings[] = include( 'settings/class-mp-settings-seller-login.php' );			self::$settings = apply_filters( 'marketplace_get_settings_pages', $settings );		}		return self::$settings;	} } ?>