<?php		class setCommition		{				function setcommision_layout($sid)				{				if(is_admin())				{				global $wpdb;				if(isset($_POST['commision']))				{				$wpdb->get_results("update {$wpdb->prefix}mpcommision set commision_on_seller='".$_POST['commision']."' where seller_id=$sid");				}				$com_res=$wpdb->get_results("select * from {$wpdb->prefix}mpcommision  where seller_id=$sid");				$cur_symbol=get_woocommerce_currency_symbol(get_option('woocommerce_currency'));				?>					<div class="wrap">						<h1> <?php echo __('Set Seller Commision', 'marketplace'); ?> </h1>						<p><hr></p>						<form action='' method='post' class="form-table" name='commision-form'>						<table>							<tr>								<th><label for='commision'><?php echo __('Commision in fixed Rate', 'marketplace'); ?></label></th>								<td><input type='text' title='commision in %' class="regular-text" name='commision' value='<?php echo $com_res[0]->commision_on_seller.' %';?>' id='commisionid' /></td>							</tr>							<tr><th><label for='totalsale'><?php echo __('Total Sale', 'marketplace'); ?></label></th>								<td><input type='text' name='totalsale' class="regular-text" value='<?php echo $com_res[0]->last_com_on_total.' '.$cur_symbol;?>' id='totalsale' readonly /></td>							</tr>							<tr><th><label for='sellerAmmount'><?php echo __('Seller Revenue', 'marketplace'); ?></label></th>								<td><input type='text' name='sellerAmmount' class="regular-text" value='<?php echo $com_res[0]->seller_total_ammount.' '.$cur_symbol;?>' id='sellerAmmount' readonly /></td>							</tr>							<tr><th><label for='admincommi'><?php echo __('Admin Commission', 'marketplace'); ?></label></th>								<td><input type='text' name='admincommi' class="regular-text" value='<?php echo $com_res[0]->admin_amount.' '.$cur_symbol;?>' id='admincommi' readonly /></td>							</tr>							<tr><th><label for='currentcomm'><?php echo __('Existing Commission', 'marketplace'); ?></label></th>								<td><input type='text' name='currentcomm' class="regular-text" value='<?php echo $com_res[0]->commision_on_seller.' %';?>' id='currentcomm' readonly /></td>							</tr>						</table>						<p><input type="submit" value="Save" class="button button-primary" /><a href="<?php echo admin_url('admin.php?page=Commissions'); ?>" class="button button-primary" style="margin-left:10px;">Back</a></p>						</form>					</div>		<?php		}	}}$res=new setCommition();$res->setcommision_layout($_GET['sid']);?>