<?php/** * Handle frontend forms * * @class 		MP_Frontend_Scripts * @version		1.0.1 * @package		Marketplace/Classes/ * @category	Class * @author 		webkul */class MP_Frontend_Scripts {	function __construct()	{		add_action( 'wp_enqueue_scripts', array( $this, 'register_marketplace_styles' ) );		add_action( 'wp_enqueue_scripts',array( $this, 'add_jQuery_libraries' ) );	}	public function register_marketplace_styles()	{		wp_register_style( 'marketplace-style', WK_MARKETPLACE.'style.css' );		wp_register_style( 'datatable-css', WK_MARKETPLACE.'assets/css/datatable.css' ); 				wp_enqueue_style( 'marketplace-style');		wp_enqueue_style('datatable-css');		/*wp_enqueue_style('font-css');*/	}	public function add_jQuery_libraries() {	    // Registering Scripts	     //wp_register_script('google-hosted-jquery', '//ajax.googleapis.com/ajax/libs/jquery/1.10.2/jquery.min.js', false);	     wp_register_script('jquery', 'http://code.jquery.com/jquery-1.11.1.min.js');		 wp_enqueue_script('jquery');		wp_register_script('pagination-js', WK_MARKETPLACE.'assets/js/pagination_datatable.js' );		wp_enqueue_script('pagination-js');		wp_register_script('datatable-cdn', WK_MARKETPLACE.'assets/js/datatable-cdn-js.js' );		wp_enqueue_script('datatable-cdn');	 	wp_register_script('easy-js', WK_MARKETPLACE.'assets/js/easying.js' );	 	wp_enqueue_script('easy-js');	 	 	wp_register_script('bsxlider', WK_MARKETPLACE.'assets/js/jquery.bxslider.min.js' );	 	wp_enqueue_script('bsxlider');//wp_enqueue_script('google-hosted-jquery');  }}new MP_Frontend_Scripts();